function isPrime(num) {
  // Checking for edge cases: 0, 1 and negative numbers are not prime numbers
  if (num <= 1) return false;

  // Checking for numbers from 2 to the square root of the number
  for (let i = 2; i <= Math.sqrt(num); i++) {
