\usepackage[utf8]{inputenc}
\usepackage{lipsum} % for filler text

% Create a new command for the section title with a custom format
\titleformat{\section}[block]{\large\bfseries}{\thesection.}{1em}{}

% Set up the table of contents
\setcounter{secnumdepth}{3} % Set the depth of sections to be numbered in the table of contents
\setcounter{tocdepth}{3} % Set the depth of sections to be included in the table of contents
\setlength{\parindent}{0pt} % Remove indentation in the table of contents
